def count(journalists)
puts "Il y a #{journalists.length} journalistes"
end 


def minimum(journalists)
  puts "C'est pas la taille qui compte, mais on sait que: 
  #{journalists.sort_by(&:length)[0]} est le plus petit"
end


def size(journalists)
puts "Il y a :
#{journalists.count {|x| x.length == 6 }} journalistes dont le pseudo contient 5 lettres"
end


def capital_letters(journalists)
puts "ça c'est le nombre de personnes dont la première lettre de pseudo est en majuscule. Cette phrase ne veut pas dire grand chose, mais on est dev, pas auteurs"
maj_letters = ('A'..'Z')
puts journalists.count {|x| maj_letters.include?(x[1])}
end


def order(journalists)
  puts "ça, c'est la liste rangée par ordre alphabétique: "  
  puts journalists.sort_by { |x| x.downcase}
  end
  

def order(journalists)
  puts "On sait que la taille ne veut pas dire grand chose, mais pour ceux que ça intéresse, là on a rangé tout ce petit monde du plus petit au plus grand: "
  puts journalists = journalists.sort_by { |x| x.length }
  end
  

  def find(journalists)
    puts "FYI @epenser se trouve dans cette position: "
    puts journalists.index("@epenser")
  end
  

  def prout(journalists)
    i = 0
    journalists.max { |a, b| a.length <=> b.length }
    while (i <= 33)
      print "nombre d'handles avec #{i} caractères: "
      puts journalists.count {|a| a.length == i}
      i+=1
    end
  end
  
def perform(journalists)
count(journalists)
minimum(journalists)
size(journalists)
capital_letters(journalists)
order(journalists)
find(journalists)
prout(journalists)
end
perform(journalists)

